import {SET_POST,SET_LOADING, SET_PAGING} from './typesPost';

export function set_Post(posts)
{
    return {
        type:SET_POST,  
        payload:posts
    }
}

export function set_Loading (loaded)
{
    return {
        type: SET_LOADING,
        payload: loaded
    }
}


export function set_Page(page) //for paging
{
    return {
        type:SET_PAGING,
        payload: page
    }
}
