import React, { useState , useEffect } from 'react';
import './Posts.css';
import {
  Button,

  Form,

} from 'semantic-ui-react'

import {Link}  from "react-router-dom";
 const FormPost  = (props)=>{
  const [modalPost, setModalPost] = useState({
    header:'',
    description:'',
    UrlImg:'',
    location:'',
    dateCreated:'',
    viewCount:0,
    tags:[],
  });
  
  useEffect(() => {
      setModalPost(props.Post);
  }, [props.Post]);

  const inputGroupChangeHandler = (event) => {
  setModalPost((prevState) => ({
    ...prevState,
    [event.target.id]: event.target.value
    }));
  }
  return (
  <Form size='huge' className='cr-up-form' onSubmit={event => {
    console.log('submit');
    event.preventDefault();
    }}>

    <Form.Group  widths={2} className='form-cr-up-post'>
      <Form.Input label='Назва'  type="text" id='header' width={8}
           name="header"value={!modalPost ? '' : modalPost.header}
          onChange={event => inputGroupChangeHandler( event)}/>
      <Form.Input label='Місце знаходження'  type="text" id='location' width={8}
        name="location"value={!modalPost ? '' : modalPost.location}
      onChange={event => inputGroupChangeHandler( event)}/>
       </Form.Group>
      <Form.Group>
      <Form.Input  label='Силка на фото'  type="text" id='UrlImg' width={6}
           name="UrlImg"value={!modalPost ? '' : modalPost.UrlImg}
          onChange={event => inputGroupChangeHandler( event)}/>
      </Form.Group>
      <Form.Group>
      <Form.TextArea label='Опис'  type="text" id='description' width={16}
           name="description" value={!modalPost ? '' : modalPost.description}
          onChange={event => inputGroupChangeHandler( event)}/>
      
    </Form.Group>
      <div className = 'cr-up-submit-button'>
        <Button as={Link} to="/" type='submit' onClick={()=>{ props.SubmitPostForm(modalPost)}} variant="primary"   >
          Зберегти
        </Button>
      </div>
    
  </Form>
    );
};
export default FormPost;
