import {LOG_OUT,SET_USER,SET_MESSAGE} from '../User/typeUser';

const defaultState = {
    loggedIn: false,
    info: {},
    errorMessage:null
}

const userReducer = (state = defaultState, action) => {
    switch(action.type){
        case SET_USER:
            debugger;
            return {
                loggedIn: true,
                user: {...action.payload}
            }
        case LOG_OUT:
            localStorage.clear()
            return {
                loggedIn: false,
                user: {}
            }
        case SET_MESSAGE:
            return{
                ...state,errorMessage:action.payload
            }
        default: return state
    }
}

export default userReducer
