import React from 'react';
import './Header.css';
import { Button } from 'semantic-ui-react'
import {Link}  from "react-router-dom";
import { Image } from 'semantic-ui-react'
import mainlogo from '../../public_img/logo.jpg'
import loginLogo from '../../public_img/userIcon.png'
import { Dropdown, Icon } from 'semantic-ui-react'

function Header() {

  return (
    <div className='Header'>
      <div className='container-icon'>
          <Image as={Link} to='/'  src={mainlogo}  circular />
      </div>
      <div className='container-auth'>
          <Image as = {Link} to='/login' src={loginLogo} />
          <div role="listbox" aria-expanded="false" class="ui dropdown" tabindex="0"><span><i aria-hidden="true" class="user icon"></i> Hello, Bob</span><i aria-hidden="true" class="dropdown icon"></i><div class="menu transition"><div style="pointer-events:all" role="option" aria-disabled="true" aria-checked="false" aria-selected="false" class="disabled item"><span class="text">Signed in as <strong>Bob Smith</strong></span></div><div style="pointer-events:all" role="option" aria-checked="false" aria-selected="true" class="selected item"><span class="text">Your Profile</span></div><div style="pointer-events:all" role="option" aria-checked="false" aria-selected="false" class="item"><span class="text">Your Stars</span></div><div style="pointer-events:all" role="option" aria-checked="false" aria-selected="false" class="item"><span class="text">Explore</span></div><div style="pointer-events:all" role="option" aria-checked="false" aria-selected="false" class="item"><span class="text">Integrations</span></div><div style="pointer-events:all" role="option" aria-checked="false" aria-selected="false" class="item"><span class="text">Help</span></div><div style="pointer-events:all" role="option" aria-checked="false" aria-selected="false" class="item"><span class="text">Settings</span></div><div style="pointer-events:all" role="option" aria-checked="false" aria-selected="false" class="item"><span class="text">Sign Out</span></div></div></div>
      </div>
      <div className = 'container-add-template'>
      <Button  as={Link} to="/addPosts"  className = 'button-add-temolate '>Додати оголошення</Button>
      </div>
    </div>
  );
}

export default Header;
