import React, { useEffect } from 'react';
import './Posts.css';
import { useSelector, useDispatch } from 'react-redux';
import {GetPosts} from '../../services/post_services';
import {Link}  from "react-router-dom";
import { Card, Icon, Image } from 'semantic-ui-react'
import  'moment/locale/ru';
import Moment from 'react-moment'

function Posts() {
  
  const dispatch = useDispatch();
    
  useEffect(() => {
      GetPosts(dispatch);
      }, []);

  const posts = useSelector(state => state.posts.posts);
  const loading = useSelector(state => state.posts.loading)
  if(loading === false)
      return<div>loading...</div>
  return (
    <div className="posts">
      {
        
        posts.map(post =>
          
          
            <Card className = 'list-post'key = {post._id.toString()}>
                  <div className = 'img-post'><Image src= {post.UrlImg}></Image></div>
                  <Card.Content className ='post-header'>
                    <Card.Header>
                      <Link  to={`/posts/${post._id}`}><span>{post.header}</span> </Link>
                    </Card.Header>
                  </Card.Content>
                  <Card.Content extra className='post-date'>
                    <Moment  date ={post.dateCreated} format ="MMM Do YY"> </Moment>
                  </Card.Content>
                
            </Card>
         
          )
         
      }
        
    </div>
  );
}

export default Posts;
