import React, { useState } from 'react';
import './Posts.css';
import {new_Post,edit_Post} from '../../redux/actions'
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { Form, Button, InputGroup, FormControl  } from "react-bootstrap";

function EditPost(props) {
  const [modalPost, setModalPost] = useState({});
   
  return (
    <div className="cr-up-post">
          <Form onSubmit={event => {
                event.preventDefault();
                props.handleFormSubmit(modalPost);
            }}>    
              <InputGroup>
                    <FormControl value={modalPost === null ? '' : modalPost.value}
                        onChange={event => setModalPost({ ...modalPost, value: event.target.value })} />
              </InputGroup>
          
          
              <Button variant="secondary" onClick={handleClose}>
                  Close
              </Button>
              <Button type='submit' variant="primary" onClick={handleClose}>
                    Save
              </Button>
          </Form>
    </div>
  );
}
let mapDispatchToProps=(dispatch)=>{
  return {
      addNewPost:(newPost)=>{
          dispatch(new_Post(newPost))
        },
      updatePost:(post)=>{
        dispatch(edit_Post(post))
      },
  }
};

let mapStateToProps=(state)=>{

  return{
      posts:state.posts,


  }
};

EditPost.propTypes = {
  editMode: PropTypes.bool,
  post: PropTypes.object
}
const CreateEditPost = connect(mapStateToProps,mapDispatchToProps) (EditPost);
export default CreateEditPost;
