import { set_Post,delete_Post,edit_Post ,new_Post, set_Loading} from "../redux/Post/actionsPost";


export const urlApi = 'http://localhost:3000/api';
export const GetPosts =  (page) => async dispatch=>{ 

    try {
        const requestOptions = {
            method: 'POST',
            mode:'cors',
            headers: { 
                "Content-type": "application/json; charset=UTF-8",
            },
            body: JSON.stringify({
                    match:{},
                    sort:{},
                    limit: 10,
                    page: page
                })
        };
        debugger;
        fetch(urlApi+'/post/filterPosts', requestOptions)
            .then(res => res.json())
            .then(data => {
                debugger;
                dispatch(set_Post(data));
                dispatch(set_Loading(true));
            })
            .catch((e)=>{
                debugger;
                console.log(e);
            });
           
            
        } catch{
            dispatch(set_Loading(false));
            console.log('Error!');
        }
    }

 

export const GetPost =(index)=> async (dispatch) =>
{
    
    try {
        
        const requestOptions = {
            method: 'GET',
            mode:'cors',
            headers: { 
                "Content-type": "application/json; charset=UTF-8",
            }
        };
       fetch(urlApi+'/post/getById/'+index, requestOptions)
            .then(res => res.json())
            .then(data => {
                
                dispatch(new_Post(data)); 
                dispatch(set_Loading(true));
            })
            .catch((e)=>{
                
                console.log(e);
            });
           
       
        }  catch{
            console.log('Error!');
        }
}


export const DeletePost = (post) => async (dispatch) =>
{
    try {
        
        const requestOptions = {
            method: 'DeLETE',
            mode:'cors',
            query:post._id,
            headers: { 
                "Content-type": "application/json; charset=UTF-8",
            }
        };
       fetch(urlApi+'/post/removePost', requestOptions)
            .then(res => res.json())
            .then(data => {
                
            })
            .catch((e)=>{
                
                console.log(e);
            });
           
       
        }   catch{
        console.log('Error!');
    }
}
export const NewPost =(post) => async (dispatch) =>
{
    try {
        post.author="tesast@mail.com";
        post._id  = null;
        post.location ={};
        const requestOptions = {
            method: 'PUT',
            mode:'cors',
            headers: { 
                "Content-type": "application/json; charset=UTF-8",
            },
            body: JSON.stringify({
                    ...post
                })
        };
        fetch(urlApi+'/post/createPost', requestOptions)
            .then(res => res.json())
            .then(data => {
                const res = {data};
                dispatch(new_Post(res));
            })
            .catch((e)=>{
                debugger;
                console.log(e);
            });
           
        } catch{
        dispatch(set_Loading(false));
        console.log('Error!');
    }
        
} 

export const EditPost = (post)=>async (dispatch) =>
{
    try {
        //api call
        try {
            post.author="tesast@mail.com";
            post.location ={};
            const requestOptions = {
                method: 'patch',
                mode:'cors',
                headers: { 
                    "Content-type": "application/json; charset=UTF-8",
                },
                body: JSON.stringify({
                        ...post
                })
            };
            debugger;
            fetch(urlApi+'/post/updatePost', requestOptions)
                .then(res => res.json())
                .then(data => {
                    debugger;
                    const res = {data};
                    dispatch(edit_Post(res));
                })
                .catch((e)=>{
                    debugger;
                    console.log(e);
                });
               
            } catch{
            dispatch(set_Loading(false));
            console.log('Error!');
        }
    } catch{
        console.log('Error!');
    }
}
