import React,{useEffect, useState} from 'react'
import { Form, Button } from 'semantic-ui-react'
import './Auth.css'
import { connect } from 'react-redux';
import {updateUser} from '../../services/user_services'

const SettingComp = (props) =>{
    console.log(props.user)
    const [userInfo, setUserInfo] = useState({
        name:"",
        phone:""
    });
    useEffect(() => {
        setUserInfo(props.user);
    }, [props.user]);

    const saveChanges = ()=>{
        props.updateUser(userInfo);
    }
    const handleOnChange = (e) => {
        e.persist();
        setUserInfo((prefState) => ({
            ...prefState,
            [e.target.name]: e.target.value 
        }))
    } 
    return (
        <div>
            <Form className='setting-form'>
                <Form.Group widths='equal'>
                    <Form.Input fluid id='form-subcomponent-shorthand-input-first-name' label='Ім`я' onChange={handleOnChange} value = { !userInfo?  "" : userInfo.name} name ='name' placeholder='Ім`я' />
                    <Form.Input fluid id='form-subcomponent-shorthand-input-last-name'label='Телефон' onChange={handleOnChange} value = { !userInfo?  "" : userInfo.phone} name ='phone' placeholder='Телефон'/>
                </Form.Group>
                <div className = 'setting-button' >
                    <Button onClick={saveChanges}>Зберегти</Button>
                </div>
            </Form>
        </div>
    )
}

let mapDispatchToProps=(dispatch)=>{
    return {
        updateUser:(userInfo)=>{
          
           dispatch(updateUser(userInfo))
        }
  }
};

let mapStateToProps=(state)=>{
  return{
    user : state.user.user,
  }
};
const Setting = connect(mapStateToProps,mapDispatchToProps) (SettingComp);

export default Setting;
