import React, { useState , useEffect } from 'react';
import './Posts.css';
import {new_Post,edit_Post} from '../../redux/actions'
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import {
  Button,
  Checkbox,
  Form,
  Input,
  Radio,
  Select,
  TextArea,
} from 'semantic-ui-react'
import { useSelector, useDispatch } from 'react-redux';

import {Link}  from "react-router-dom";
import { GetPost } from '../../services/post_services';

function EditPost(props) {
  const dispatch = useDispatch();
  let post = props.post; 
  let id = props.match.params.id;
    useEffect(() => {
        GetPost(dispatch,id);
        }, []);

  const [modalPost, setModalPost] = useState({
                                    header:'',
                                    description:'',
                                    UrlImg:'',
                                    location:'',
                                    createDate:''
                                  });
  const  SubmitPostFormCreate = ()=> {
    
    modalPost.id=10;
    if(modalPost.Name !== ''){
      debugger;
      props.addNewPost(modalPost);
    }
    else{
      console.log('error');
    }
       

  }

  const SubmitPostFormUpdate = () => {
    modalPost.id=10;
    if(modalPost.Name !== ''){
      props.updatePost(modalPost);
    }
    else{
      console.log('error');
    }
       
  }
  post=useSelector(state => state.posts.posts.find(i => i._id == props.id));

  debugger;
  if(props.match.params.id && post)
  {
    
    
    
    return <CrUpPost modalPost={modalPost} setModalPost={setModalPost} SubmitPostForm = {SubmitPostFormUpdate}/>
  }
  return <CrUpPost modalPost={modalPost} setModalPost={setModalPost} SubmitPostForm = {SubmitPostFormCreate}/>
}

const CrUpPost  = ({modalPost,setModalPost,SubmitPostForm})=>{
    
  
  
  const inputGroupChangeHandler = (event) => {
  setModalPost((prevState) => ({
  ...prevState,
  [event.target.id]: event.target.value
  }));
  }
  return (
  <Form  onSubmit={event => {
    console.log('submit');
    event.preventDefault();
    }}>

    <Form.Group>
      <Form.Input label='Назва'  type="text" id='header' width={6}
           name="header"value={modalPost === null ? '' : modalPost.value}
          onChange={event => inputGroupChangeHandler( event)}/>

      

      <Form.Input label='Опис'  type="text" id='description' width={6}
           name="description" value={modalPost === null ? '' : modalPost.value}
          onChange={event => inputGroupChangeHandler( event)}/>
      <Form.Input label='Силка на фото'  type="text" id='UrlImg' width={6}
           name="UrlImg"value={modalPost === null ? '' : modalPost.value}
          onChange={event => inputGroupChangeHandler( event)}/>
      
    </Form.Group>
      <div className = 'cr-up-submit-button'>
        <Button as={Link} to="/" type='submit' onClick={SubmitPostForm} variant="primary"   >
        Зберегти
        </Button>
      </div>
    
  </Form>
    );
};


let mapDispatchToProps=(dispatch)=>{
      return {
          addNewPost:(newPost)=>{
            
              dispatch(new_Post(newPost))
            },
          updatePost:(post)=>{
            dispatch(edit_Post(post))
          },
    }
};

let mapStateToProps=(state)=>{
  return{
      posts:state.posts,
  }
};

EditPost.propTypes = {
  editMode: PropTypes.bool,
  post: PropTypes.object
};


const CreateEditPost = connect(mapStateToProps,mapDispatchToProps) (EditPost);
export default CreateEditPost;
