import React, { useEffect } from 'react';
import './Posts.css';
import { useSelector, useDispatch } from 'react-redux';
import {GetUsersPosts} from '../../services/user_services';
import {Link, Redirect}  from "react-router-dom";
import { Card, Image } from 'semantic-ui-react'
import Moment from 'react-moment'
import  'moment/locale/ru';
import { Pagination } from 'semantic-ui-react';
import {setUserPaging} from '../../redux/User/userActions';

function UserPosts() {
  let user =  useSelector(state => state.user);
  
  const dispatch = useDispatch();
  const page = useSelector(state => state.user.page);
  useEffect(() => {
      dispatch(GetUsersPosts(page,user.user.email));
      }, []);
  const posts = useSelector(state => state.user.posts);
  const loading = useSelector(state => state.user.loading);
  const onchangePage =(e, {activePage})=>{
    dispatch(setUserPaging(activePage-1));
  
  }
  if(!user.loggedIn)
  {
    return (<Redirect to='/login'/>)
  }
  if(loading === false)
      return<div>loading...</div>
  return (
    <div className="posts">
      <Card.Group>
      {
        posts.map(post =>
            <Card key = {post._id}>
                    <Card.Content>
                    <Image className='imgpost' src= {post.UrlImg}></Image>
                     </Card.Content>
                    <Card.Content extra>
                      <Card.Header  >
                      <Link  to={`/posts/${post._id}`}><span className='posts-header'>{post.header}</span> </Link>
                    </Card.Header>
                    <div>
                      <span>{post.location +" "}</span>
                      <Moment  date ={post.dateCreated} format ="MMM Do YY"> </Moment>
                    </div>
                    </Card.Content>
            </Card>
          )
      }
          </Card.Group>
          <div className='post-paging'> <Pagination  defaultActivePage={1}  totalPages={10} onPageChange = {onchangePage} /></div>
    </div>
  );
}
  {/*
  EditPost.propTypes = {
  editMode: PropTypes.bool,
  post: PropTypes.object
  };*/}
  
  export default UserPosts;
