import { set_Post,delete_Post,edit_Post ,new_Post} from "../redux/postReducer";

export const GetPosts = async (dispatch) =>
{
    try {
        //api call
        const response = [
            {value:'Study for exam in 3 weeks', id:1},
            {value:'I will be a master in no time', id:2},
            {value:'Build more full-stack application', id:3},
            {value:'I love writting notes', id:4},
           
        ]
        dispatch(set_Post(response));
    } catch{
        console.log('Error!');
    }
}

export const DeleteNote = async (dispatch,post) =>
{
    try {
        //api call
        
        dispatch(delete_Post(post));
    } catch{
        console.log('Error!');
    }
}
export const NewNote = async (dispatch,post) =>
{
    try {
        //api call
        const response = {value:post, id:5};
        dispatch(new_Post(response));
    } catch{
        console.log('Error!');
    }
}
export const EditNote = async (dispatch,post) =>
{
    try {
        //api call
        
        const response = {value:post, id:1};
        dispatch(edit_Post(response));
    } catch{
        console.log('Error!');
    }
}
