import React, { useState , useEffect } from 'react';
import {new_Post,edit_Post} from '../../redux/actions'
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import FormPost from './Posts'


const CreatePost = function (props)
{
      
  const [modalPost, setModalPost] = useState({
                                    _id:'',
                                    header:'',
                                    description:'',
                                    UrlImg:'',
                                    location:'',
                                    dateCreated:'',
                                    viewCount:0,
                                    tags:[],
                                  });
  const  SubmitPostFormCreate = ()=> {
    
    modalPost.id=10;
    if(modalPost.Name !== ''){
      debugger;
      props.addNewPost(modalPost);
    }
    else{
      console.log('error');
    }
       

  }
  return <FormPost modalPost={modalPost} setModalPost={setModalPost} SubmitPostForm = {SubmitPostFormCreate}/>
}
  let mapDispatchToProps=(dispatch)=>{
      debugger;
    return {
        addNewPost:(newPost)=>{
          
            dispatch(new_Post(newPost))
          },
        updatePost:(post)=>{
          dispatch(edit_Post(post))
        },
  }
};

let mapStateToProps=(state)=>{
    return{
        posts:state.posts,
    }
};







const CreatePostForm = connect(mapStateToProps,mapDispatchToProps) (CreatePost);
export default CreatePostForm;
