import React from 'react';
import './Header.css';
import { Button } from 'semantic-ui-react'
import {Link}  from "react-router-dom";
import { Image } from 'semantic-ui-react'
import mainlogo from '../../public_img/logo.jpg'
import loginLogo from '../../public_img/userIcon.png'
import { Dropdown, Icon } from 'semantic-ui-react'

function Header() {
  const trigger = (
    <span>
      <Icon name='user' /> <strong>Мій профіль</strong>
    </span>
  )
  
  const options = [
    {
      key: 'user',
      text: (
        <span>
          Signed in as <strong>Bob Smith</strong>
        </span>
      ),
      disabled: true,
    },
    { key: 'profile', text: 'Your Profile' },
    { key: 'stars', text: 'Your Stars' },
    { key: 'explore', text: 'Explore' },
    { key: 'integrations', text: 'Integrations' },
    { key: 'help', text: 'Help' },
    { key: 'settings', text: 'Settings' },
    { key: 'sign-out', text: 'Sign Out' },
  ]
  
  return (
    <div className='Header'>
      <div className='container-icon'>
          <Image as={Link} to='/'  src={mainlogo}  circular />
      </div>
      <div className='container-auth'>
          <div className='container-profile'>
          <Dropdown className='prof-dropdown' trigger={trigger} options={options} />
          </div>
      
        <div className = 'container-add-template'>
          <Button  as={Link} to="/addPosts"  className = 'button-add-temolate '>Додати оголошення</Button>
        </div>
      </div>
    </div>
  );
}

export default Header;
