import React, { useState , useEffect } from 'react';
import './Posts.css';
import {new_Post,edit_Post} from '../../redux/actions'
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { useSelector, useDispatch } from 'react-redux';
import FormPost from './FormPost'
import {Link}  from "react-router-dom";
import { GetPost } from '../../services/post_services';

function EditPost(props) {
  const dispatch = useDispatch();
  let post = props.post; 
  let id = props.match.params.id;
    useEffect(() => {
        GetPost(dispatch,id);
        }, []);
        
  const [modalPost, setModalPost] = useState({
                                    _id:'',
                                    header:'',
                                    description:'',
                                    UrlImg:'',
                                    location:'',
                                    dateCreated:'',                
                                  });
  

  const SubmitPostFormUpdate = () => {
    
    console.log(modalPost);
        if(modalPost.Name !== ''){
        props.updatePost(modalPost);
        }
        else{
        console.log('error');
        }
        
    }
    debugger;
    post=useSelector(state => state.posts.posts.find(i => i._id === id));
    useEffect(() => {
        setModalPost(post);
    }, [post]);
    
    debugger;
   
    return <FormPost modalPost={modalPost} setModalPost={setModalPost} SubmitPostForm = {SubmitPostFormUpdate}/>
  
  
}
let mapDispatchToProps=(dispatch)=>{
    return {
        addNewPost:(newPost)=>{
          
            dispatch(new_Post(newPost))
          },
        updatePost:(post)=>{
          dispatch(edit_Post(post))
        },
  }
};

let mapStateToProps=(state)=>{
return{
    posts:state.posts,
}
};


{/*
EditPost.propTypes = {
editMode: PropTypes.bool,
post: PropTypes.object
};*/}



const EditPostComp = connect(mapStateToProps,mapDispatchToProps) (EditPost);
export default EditPostComp;
