import React, {  useEffect , useState} from 'react';
import {connect} from 'react-redux'
import {fetchUser} from '../../services/user_services'
import mainlogo from '../../public_img/logo.jpg'
import { Button, Form, Grid, Header, Image, Message, Segment } from 'semantic-ui-react'
const LoginComponent =(props) => {
    
    const [userInfo, setUserInfo] = useState({
        email:"",
        password:""
    });
    const handleOnChange = (e) => {
        debugger;
        e.persist();
        setUserInfo((prefState) => ({
            ...prefState,
            [e.target.name]: e.target.value 
        }))
    }

    const loginClick=(e) =>{
        debugger;
        props.fetchUser(userInfo);
    }
    return (
        <Grid textAlign='center' style={{ height: '50vh' }} verticalAlign='middle'>
            <Grid.Column style={{ maxWidth: 450 }}>
            <Header as='h2' color='rgb(25,41,64)' textAlign='center'>
            <Image src={mainlogo} circular/> Вхід у аккаунт
            </Header>
            
                <Form size='large'>
                    <Segment stacked>
                        <Form.Input fluid icon='user' name='email' iconPosition='left' value={userInfo.email}
                                onChange={handleOnChange} placeholder='E-mail' />
                        <Form.Input
                            fluid
                            icon='lock'
                            name='password'
                            iconPosition='left'
                            placeholder='Password'
                            value={userInfo.password}
                            onChange={handleOnChange}
                            type='password'
                        />
                        <Button color='black' onClick={loginClick} size='large'type="submit"  value="Війти"> Війти</Button>
                    </Segment>
                </Form>
                <Message>
                    Не має аккаунта? <a href='/SignUp'>Реєстрація</a>
                </Message>
            </Grid.Column>
        </Grid>
    )
}

const  mapStateToProps=(state)=>{
    return{
        user:state.user.errorMessage,
    }
  };

const mapDispatchToProps = (dispatch) => {
    return {
        fetchUser: (userInfo) => dispatch(fetchUser(userInfo))
    }
}


export default connect(null, mapDispatchToProps)(LoginComponent)
