import React, { useState , useEffect } from 'react';
import './Posts.css';
import {new_Post,edit_Post} from '../../redux/actions'
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { useSelector, useDispatch } from 'react-redux';
import { Form, Button, InputGroup, FormControl  } from "react-bootstrap";
import {Link}  from "react-router-dom";
import { GetPost } from '../../services/post_services';

function EditPost(props) {
  const dispatch = useDispatch();
  let post = props.post; 
    useEffect(() => {
        GetPost(dispatch,props.id);
        }, []);

  const [modalPost, setModalPost] = useState({
                                    header:'',
                                    description:'',
                                    UrlImg:'',
                                    location:'',
                                    createDate:''
                                  });
  const  SubmitPostFormCreate = ()=> {
    
    modalPost.id=10;
    if(modalPost.Name !== ''){
      debugger;
      props.addNewPost(modalPost);
    }
    else{
      console.log('error');
    }
       

  }

  const SubmitPostFormUpdate = () => {
    modalPost.id=10;
    if(modalPost.Name !== ''){
      props.updatePost(modalPost);
    }
    else{
      console.log('error');
    }
       
  }
  post=useSelector(state => state.posts.posts.find(i => i._id == props.id));

  debugger;
  if(props.match.params.id)
  {
    
    
    
    return <CrUpPost modalPost={modalPost} setModalPost={setModalPost} SubmitPostForm = {SubmitPostFormUpdate}/>
  }
  return <CrUpPost modalPost={modalPost} setModalPost={setModalPost} SubmitPostForm = {SubmitPostFormCreate}/>
}

const CrUpPost  = ({modalPost,setModalPost,SubmitPostForm})=>{
    
  
  
  const inputGroupChangeHandler = (event) => {
  setModalPost((prevState) => ({
  ...prevState,
  [event.target.id]: event.target.value
  }));
  }
  return (
  <form className='cr-up-form' onSubmit={event => {
    console.log('submit');
    event.preventDefault();
    }}>

    <div className='cr-up-input'> 
    <label >Назва </label>
    <input type="text" id='Name' name="Name"value={modalPost === null ? '' : modalPost.value}
    onChange={event => inputGroupChangeHandler( event)} />
    </div>

    <div className='cr-up-input'> 
    <label>Опис</label>
    <input type="text" id='Description' name="Description"value={modalPost === null ? '' : modalPost.value}
    onChange={event => inputGroupChangeHandler( event)} />
    </div>
    <div className='cr-up-input'> 
    <label> Силка на фото</label>
    <input type="text" id='UrlImg' name="UrlImg"value={modalPost === null ? '' : modalPost.value}
    onChange={event => inputGroupChangeHandler( event)} />
    </div>  
    <div className = 'cr-up-submit-button'>
      <Button as={Link} to="/" type='submit' onClick={SubmitPostForm} variant="primary"   >
      Зберегти
      </Button>
    </div>
  </form>
    );
};


let mapDispatchToProps=(dispatch)=>{
      return {
          addNewPost:(newPost)=>{
            
              dispatch(new_Post(newPost))
            },
          updatePost:(post)=>{
            dispatch(edit_Post(post))
          },
    }
};

let mapStateToProps=(state)=>{
  return{
      posts:state.posts,
  }
};

EditPost.propTypes = {
  editMode: PropTypes.bool,
  post: PropTypes.object
};


const CreateEditPost = connect(mapStateToProps,mapDispatchToProps) (EditPost);
export default CreateEditPost;
