import { createStore,applyMiddleware,compose} from 'redux';
import {rootReducer} from './rootReducer';
import thunk from 'redux-thunk';
import {changePageMiddleware,changeUserPageMiddleware} from '../redux/middleware/postMiddleware';

const composeEnhancer = window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose;
export const store = createStore(rootReducer,composeEnhancer(applyMiddleware(thunk,changePageMiddleware,changeUserPageMiddleware)));
