import { SET_POST, SET_LOADING, SET_PAGING } from './typesPost';

const initialState = {
    posts: [],
    loading: false,
    page: 1
}
export default function postsReducer(state = initialState, action) {
    switch (action.type) {
        case SET_POST:
            debugger;
            return { ...state, posts: [...action.payload] };
        
        case SET_LOADING:
            return { ...state, loading: action.payload }
        case SET_PAGING:
            return { ...state, page: action.payload}
        default:
            return state;
    }
}
