import React from 'react';
import './Main.css';
import viewPost from '../Posts/ViewPost'
import CreatePostForm from '../Posts/CreatePostForm'
import EditPostComp from '../Posts/UpdatePostForm'
import Posts from'../Posts/Posts'
import {Route,Switch} from 'react-router-dom'
import LoginComponent from '../User/Login';
import SignUpComponent from '../User/SignUp';
import PrivateRoute from '../Main/PrivateRouting';
import { useSelector } from 'react-redux'
import UserPosts from '../User/UsersPosts'
import { connect } from 'react-redux';
import Logout from '../User/Logout'
import Setting from '../User/Settings'


function Main(props) {

  debugger;
  return (
    <div className="Main">
    <Switch>
     <PrivateRoute path='/usersPosts' component={UserPosts}/>
     <PrivateRoute  path='/logout' component={Logout}/>
     <PrivateRoute path='/Setting' component={Setting} />
      <Route path='/SignUp' component={SignUpComponent}/> 
      <Route path='/login' component={LoginComponent}/>
      <Route path='/changePost/:id' component={EditPostComp}/>
      <Route path="/posts/:id" component={viewPost} />
      <PrivateRoute  path='/addPosts' component={CreatePostForm}/>
     
      <Route path='/' component ={Posts} />
    </Switch>
    </div>
  );
}






export default Main;
