import {LOG_OUT,SET_USER} from '../User/typeUser';

const defaultState = {
    loggedIn: false,
    token:'',
    user: {}
}

const userReducer = (state = defaultState, action) => {
    switch(action.type){
        case SET_USER:
            return {
                loggedIn: true,
                token:action.payload,
                user:{}
            }
        case LOG_OUT:
            localStorage.clear()
            return {
                loggedIn: false,
                user: {}
            }
        default: return state
    }
}

export default userReducer
