import React from 'react'
import {connect} from 'react-redux'
import {signUserUp} from '../../redux/User/userActions'
import { Button, Form, Grid, Header, Image, Message, Segment } from 'semantic-ui-react'


class SignUpComponent extends React.Component {
    state = {
        username: "",
        password: "",
        age: ""
    }

    handleOnChange = (e) => {
        e.persist();
        this.setState(() => ({
            [e.target.name]: e.target.value 
        }))
    }

    onSubmit = (e) => {
        e.preventDefault()
        this.props.signUserUp(this.state)
    }

    render(){
        return(
            
            
        <Grid textAlign='center' style={{ height: '50vh' }} verticalAlign='middle'>
            <Grid.Column style={{ maxWidth: 450 }}>
            <Header as='h2' color='rgb(25,41,64)' textAlign='center'>
                Реєстрація
            </Header>
            
                <Form size='large'>
                    <Segment stacked>
                        <Form.Input  value={this.userInfo.username}
                                onChange={this.handleOnChange} placeholder='Name' />
                        <Form.Input value={this.userInfo.email}
                                onChange={this.handleOnChange} placeholder='E-mail' />
                        <Form.Input value={this.userInfo.phone}
                            onChange={this.handleOnChange} placeholder='phone' />
                        <Form.Input
                            fluid
                            icon='lock'
                            iconPosition='left'
                            placeholder='Password'
                            value={this.userInfo.password}
                            onChange={this.handleOnChange}
                            type='password'
                        />
                        <Button color='black' size='large'type="submit"  value="Війти"> Підтвердити</Button>
                    </Segment>
                </Form>
            </Grid.Column>
        </Grid>
        )
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        signUserUp: (userInfo) => dispatch(signUserUp(userInfo))
    }
}

export default connect(null, mapDispatchToProps)(SignUpComponent)
