import {LOG_OUT,SET_USER} from '../User/typeUser';

const defaultState = {
    loggedIn: false,
    user: {},
    error:null
}

const userReducer = (state = defaultState, action) => {
    switch(action.type){
        case SET_USER:
            debugger;
            return {
                loggedIn: true,
                user: {...action.payload}
            }
        case LOG_OUT:
            localStorage.clear()
            return {
                loggedIn: false,
                user: {}
            }
        default: return state
    }
}

export default userReducer
