import {setUser} from './../redux/User/userActions'
import {urlApi} from './post_services'
export const fetchUser = (userInfo) => dispatch => {
    try {
        const requestOptions = {
            method: 'POST',
            headers: { 
                "Content-type": "application/json; charset=UTF-8"
            },
            body: JSON.stringify(userInfo)
        };
        fetch( urlApi+`/users/login`,requestOptions)
        .then(res => res.json())
        .then(data => {
        debugger;
            localStorage.setItem("token", data.currentToken)
            dispatch(setUser(data.currentToken))
        })
        .catch((e)=>{
            console.log(e);
        });
        } catch{
        
        console.log('Error!');
    }
}

export const signUserUp = (userInfo) => dispatch => {
    try {
        fetch(urlApi+`/users/createNewUser`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json"
            },
            body: JSON.stringify(userInfo)
        })
        .then(res => res.json())
        .then(data => {
        
            debugger;
            localStorage.setItem("token", data.currentToken)
            dispatch(setUser(data.user))
        })
        .catch((e)=>{
            console.log(e);
        });
    } catch{
            
        console.log('Error!');
    }
}

export const autoLogin = () => dispatch => {
    try {
        fetch(`urlApi/auto_login`, {
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
                "Authorization": `Bearer ${localStorage.getItem("token")}`
            }
        })
        .then(res => res.json())
        .then(data => {
            
            localStorage.setItem("token", data.token)
            dispatch(setUser(data.user))
        })
        .catch((e)=>{
            console.log(e);
        });
    } catch{
            
        console.log('Error!');
    }
}
