import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { useSelector, useDispatch } from 'react-redux';
import { Container, Header,Divider,Button } from 'semantic-ui-react';
import {Link} from 'react-router-dom';
import Moment from 'react-moment'
import  'moment/locale/ru';
import { GetPost } from '../../services/post_services';


const ViewPost = function (props) {
    const dispatch = useDispatch();
     let post = props.post; 
    useEffect(() => {
        GetPost(dispatch,props.id);
        }, []);
    post=useSelector(state => state.posts.posts);
    
    if(post)
      return<div>loading...</div>
    console.log(post);
    return (
        <div className="view-post">
            <Container fluid>
                <Header as='h2'><span>{post.header}</span></Header>
                <Divider className='devider'/>
                <Header as='h2'><span>Опис</span></Header>
                <p>{post.description}</p>
                <div className='button-view-post'>
                <Button  as={Link} to="/changePost" secondary className = 'button-change-post'>Змінити</Button>
                </div>
                
                <Divider />
            <div className='footer-post-view'>
                <span>{post.location +" "}</span>
                <Moment  date ={post.dateCreated} format ="MMM Do YY"> </Moment>
            </div>
            </Container>
        </div>
    );

}

let mapStateToProps=(state,ownProps)=>{
    
    return{
        id:ownProps.match.params.id-1,
    }
  };
  
  ViewPost.propTypes = {
    post: PropTypes.object
  }
  const viewPost = connect(mapStateToProps,null) (ViewPost);
  export default viewPost;
