import React,{useState} from 'react'
import {connect} from 'react-redux'
import {signUserUp} from '../../services/user_services'
import { Button, Form, Grid, Header,  Segment } from 'semantic-ui-react'
import { Redirect } from 'react-router-dom';


const SignUpComponent = (props) => {
    const [userInfo, setUserInfo] = useState({
        name: "",
        password: "",
        email: "",
        phone:''
    });

    const [error, setError] = useState({});
    const handleOnChange = (e) => {
        e.persist();
        debugger;
        setUserInfo((prstate) => ({
            ...prstate,
            [e.target.name]: e.target.value 
        }))
    }
    var authed = props.user.loggedIn;
    if(authed)
    {
        return (<Redirect to='/usersPosts'/>);
    }

    function validate(){
        let input = this.state.input;
        let errors = {};
        let isValid = true;
    
        if (!input["name"]) {
          isValid = false;
          errors["name"] = "Please enter your name.";
        }
    
        if (!input["email"]) {
          isValid = false;
          errors["email"] = "Please enter your email Address.";
        }
    
        if (typeof input["email"] !== "undefined") {
            
          var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
          if (!pattern.test(input["email"])) {
            isValid = false;
            errors["email"] = "Please enter valid email address.";
          }
        }
    
       
    
        this.setState({
          errors: errors
        });
    
        return isValid;
    }

    const onSubmit = (e) => {
        e.preventDefault()
        props.signUserUp(userInfo)
    }

    return(
    <Grid textAlign='center' style={{ height: '50vh' }} verticalAlign='middle'>
        <Grid.Column style={{ maxWidth: 450 }}>
        <Header as='h2' color='rgb(25,41,64)' textAlign='center'>
            Реєстрація
        </Header>
        
            <Form size='large'>
                <Segment stacked>
                    <Form.Input  value={userInfo.username}
                            name= 'name'
                            onChange={e=> handleOnChange(e)} placeholder='Name' />
                    <Form.Input value={userInfo.email}
                            name ='email'
                            onChange={e=> handleOnChange(e)} placeholder='E-mail' />
                    <Form.Input value={userInfo.phone}
                        name ='phone'
                        onChange={e=> handleOnChange(e)} placeholder='phone' />
                    <Form.Input
                        fluid
                        icon='lock'
                        iconPosition='left'
                        name ='password'
                        placeholder='Password'
                        value={userInfo.password}
                        onChange={e => handleOnChange(e)}
                        type='password'
                    />
                    <Button color='black' size='large'type="submit"  value="Війти" onClick={onSubmit}> Підтвердити</Button>
                </Segment>
            </Form>
        </Grid.Column>
    </Grid>
    )
}

const  mapStateToProps=(state)=>{
    return{
        user:state.user,
    }
  };


const mapDispatchToProps = (dispatch) => {
    return {
        signUserUp: (userInfo) => dispatch(signUserUp(userInfo))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(SignUpComponent)
