import React,{useState,useRef, useEffect} from 'react'
import {connect} from 'react-redux'
import {signUserUp} from '../../services/user_services'
import {isNullOrEmpty} from '../../services/services_custom'
import { Button, Form, Grid, Header,  Segment } from 'semantic-ui-react'
import { Redirect } from 'react-router-dom';

export const useIsMount = () => {
  const isMountRef = useRef(true);
  useEffect(() => {
    isMountRef.current = false;
  }, []);
  return isMountRef.current;
};

const SignUpComponent = (props) => {
    const [userInfo, setUserInfo] = useState({
        name: "",
        password: "",
        email: "",
        phone:''
    });
    const [errorNameInput,setErrorInputdName] = useState({});
    const [errors, setErrors] = useState({});
    const handleOnChange = (e) => {
        
            e.persist();
            setUserInfo((prstate) => ({
                ...prstate,
                [e.target.name]: e.target.value 
            }));
            setErrorInputdName(()=>({
                ["NameInput"] : e.target.name
            }));
    }
    const isMount = useIsMount();

    useEffect(() => {
        debugger;
        if(!isMount)
        {
           validate(errorNameInput.NameInput); 
        }
     }, [userInfo,errorNameInput.NameInput]);

    var authed = props.user.loggedIn;
    if(authed)
    {
        return (<Redirect to='/usersPosts'/>);
    }
    const setErrorValue = (nameInput,value) =>
    {
        debugger;
        setErrors((preState)=>({
            ...preState,
            [nameInput]:value
        }))
    }
    function validate(nameInput = null){
        let input = userInfo;
        let isValid = true;
        let name = null;
        switch(nameInput)
        {
            case "name":
                name = nameInput;
                break;
            case "phone":
                name = nameInput;
                break;
            case "email":
                name = nameInput;
                break;
            default :
                name = null;
        }
        debugger;
        if ( isNullOrEmpty( input["name"]) && ((!name) || name === 'name')) {
          isValid = false;
          setErrorValue ("name" , "Будь ласка ведіть ім'я");
        }
        
        if (isNullOrEmpty(input["phone"]) && ((!name) || name === 'phone')) {
            isValid = false;
            setErrorValue ("phone" , "Будь ласка ведіть телефон");
        }

        if (isNullOrEmpty(input["email"]) && ((!name) || name === 'email')) {
          isValid = false;
          setErrorValue ("email" , "Будь ласка ведіть почту");
        }
    
        if (typeof input["email"] !== "undefined" && ((!name) || name === 'email')) {
            
          var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
          if (!pattern.test(input["email"])) {
            isValid = false;
            setErrorValue ("email" , "Будь ласка ведіть валідну почту");
          }
        }
    
       
    
    
        return isValid;
    }

    const onSubmit = (e) => {
        e.preventDefault();
        if(validate())
        {
           props.signUserUp(userInfo) 
        }
        
    }

    return(
    <Grid textAlign='center' style={{ height: '50vh' }} verticalAlign='middle'>
        <Grid.Column style={{ maxWidth: 450 }}>
        <Header as='h2' color='rgb(25,41,64)' textAlign='center'>
            Реєстрація
        </Header>
        
            <Form size='large'>
                <Segment stacked>
                    <Form.Input  value={userInfo.username}
                            name= 'name'
                            onChange={e=> handleOnChange(e)} placeholder={errors.name} />
                    <div className="text-danger">{errors.name}</div>
                    <Form.Input value={userInfo.email}
                            name ='email'
                            onChange={e=> handleOnChange(e)} placeholder={errors.email} />
                    <div className="text-danger">{errors.email}</div>
                    <Form.Input value={userInfo.phone}
                        name ='phone'
                        onChange={e=> handleOnChange(e)} placeholder={errors.phone}/>
                    <div className="text-danger">{errors.phone}</div>

                    <Form.Input
                        fluid
                        icon='lock'
                        iconPosition='left'
                        name ='password'
                        placeholder='Password'
                        value={userInfo.password}
                        onChange={e => handleOnChange(e)}
                        type='password'
                    />
                    <Button color='black' size='large'type="submit"  value="Війти" onClick={onSubmit}> Підтвердити</Button>
                </Segment>
            </Form>
        </Grid.Column>
    </Grid>
    )
}

const  mapStateToProps=(state)=>{
    return{
        user:state.user,
    }
  };


const mapDispatchToProps = (dispatch) => {
    return {
        signUserUp: (userInfo) => dispatch(signUserUp(userInfo))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(SignUpComponent)
