import {SET_NOTES,DELETE_NOTE,NEW_NOTE,EDIT_NOTE,SET_LOADING, SET_PAGING} from './typesPost';

export function set_Post(posts)
{
    return {
        type:SET_NOTES,  
        payload:posts
    }
}

export function delete_Post(post) 
{
    return {
        type:DELETE_NOTE,
        payload: post
    }
}

export function new_Post (post)
{
    debugger;
    return {
        type: NEW_NOTE,
        payload: post
    }
}
export function set_Loading (loaded)
{
    return {
        type: SET_LOADING,
        payload: loaded
    }
}

export function edit_Post(post) //rewrite for api
{
    return {
        type:EDIT_NOTE,
        payload: post
    }
}
export function set_Page(page) //for paging
{
    return {
        type:SET_PAGING,
        payload: page
    }
}
