import React, { useEffect } from 'react';
import './Posts.css';
import { useSelector, useDispatch } from 'react-redux';
import {GetPosts} from '../../services/post_services';
import {Link}  from "react-router-dom";
import { Card, Icon, Image } from 'semantic-ui-react'
import Moment from 'react-moment'
import  'moment/locale/ru';
import { Pagination } from 'semantic-ui-react'

function Posts() {
  
  const dispatch = useDispatch();
    
  useEffect(() => {
      GetPosts(dispatch);
      }, []);

  const posts = useSelector(state => state.posts.posts);
  const loading = useSelector(state => state.posts.loading)
  if(loading === false)
      return<div>loading...</div>
  return (
    <div className="posts">
      <Card.Group>
      {
        
        posts.map(post =>
          
            <Card key = {post._id}>
                    <Card.Content>
                    <Image className='imgpost' src= {post.UrlImg}></Image>
                    
                     </Card.Content>
                    <Card.Content extra>
                      <Card.Header  >
                      <Link  to={`/posts/${post._id}`}><span className='posts-header'>{post.header}</span> </Link>
                    </Card.Header>
                    <div>
                      <span>{post.location +" "}</span>
                      <Moment  date ={post.dateCreated} format ="MMM Do YY"> </Moment>
                    </div>
                    </Card.Content>
            </Card>
        
          )
         
        
      }
          </Card.Group>
           <Pagination className='post-padding' defaultActivePage={1} totalPages={10} />
    </div>
  );
}

export default Posts;
