import React from 'react';
import './Main.css';
import viewPost from '../Posts/ViewPostComponent'
import CreatePostForm from '../Posts/CreatePostFormComponent'
import EditPostComp from '../Posts/UpdatePostFormComponent'
import Posts from'../Posts/PostsComponent'
import {Route,Switch} from 'react-router-dom'
import LoginComponent from '../User/LoginComponent';
import SignUpComponent from '../User/SignUpComponent';
import PrivateRoute from '../Main/PrivateRouting';
import { useSelector } from 'react-redux'
import UserPosts from '../User/UsersPostsComponent'

function Main() {
  const authentication = useSelector(state => state.user.loggedIn)
  return (
    <div className="Main">
    <Switch>
     
      <Route path='/SignUp' component={SignUpComponent}/>
      <Route path='/login' component={LoginComponent}/>
      <Route path='/changePost/:id' component={EditPostComp}/>
      <Route path="/posts/:id" component={viewPost} />
      <PrivateRoute authed={authentication} path='/addPosts' component={CreatePostForm}/>
      <PrivateRoute authed={authentication} path='/usersPosts' component={UserPosts}/>
      <Route path='/' component ={Posts} />
    </Switch>
    </div>
  );
}

export default Main;
