import React, {  useEffect } from 'react';
import './Posts.css';
import {new_Post,edit_Post} from '../../redux/Post/actionsPost'
import { connect } from 'react-redux';
import { useSelector, useDispatch } from 'react-redux';
import FormPost from './FormPost'
import { GetPost,EditPost } from '../../services/post_services';

function UpdatePost(props) {
  const dispatch = useDispatch();
  let post = props.post; 
  let id = props.match.params.id;
  useEffect(() => {
    dispatch(GetPost(id));
    });
        
 
  const SubmitPostFormUpdate = function(modalPost) {
    
    
    debugger;
    if(modalPost.Name !== ''){
        dispatch(EditPost(modalPost))
    }
    else{
        console.log('error');
    }
        
    }
    debugger;
    post=useSelector(state => state.posts.posts.find(i => i._id === id));
    
    
   
    return <FormPost Post={post} SubmitPostForm = {SubmitPostFormUpdate}/>
  
  
}
let mapDispatchToProps=(dispatch)=>{
    return {
        addNewPost:(newPost)=>{
          
            dispatch(new_Post(newPost))
          },
        updatePost:(post)=>{
          dispatch(edit_Post(post))
        },
  }
};

let mapStateToProps=(state)=>{
return{
    posts:state.posts,
}
};


{/*
EditPost.propTypes = {
editMode: PropTypes.bool,
post: PropTypes.object
};*/}



const EditPostComp = connect(mapStateToProps,mapDispatchToProps) (UpdatePost);
export default EditPostComp;
