import {SET_NOTES,DELETE_NOTE,NEW_NOTE,EDIT_NOTE} from './types';

const initialState = {
    posts: [],
    loading:false
}
export default function postsReducer(state = initialState, action) {
    switch (action.type) {
        case SET_NOTES:
            return { ...state, posts: [...action.payload] };
        case DELETE_NOTE:
            return {
                ...state, posts: [...state.notes.filter(post =>
                    post.id !== action.payload.value.id)]
            }
        case NEW_NOTE:
            return { ...state, posts: [...state.posts, action.payload.value] }
        case EDIT_NOTE:
            let posts = state.posts.map(post => {
                debugger;
                if (post.id === action.payload.value.id) {
                    post = action.payload.value
                }
                return post;
            })
            return { ...state, posts: [...posts] }
        default:
            return state;
    }
}
