import {setUser,setMessage,setUserPost,setUserLoading} from './../redux/User/userActions'
import {urlApi} from './post_services'
import history from '../history';
import { Redirect } from 'react-router';
export const fetchUser = (userInfo) => dispatch => {
    try {
        const requestOptions = {
            method: 'POST',
            headers: { 
                "Content-type": "application/json; charset=UTF-8"
            },
            body: JSON.stringify(userInfo)
        };
        fetch( urlApi+`/users/login`,requestOptions)
        .then(res => res.json())
        .then(data => {
            
            if(!data.error)
            {
                localStorage.setItem("token", data.currentToken);
                dispatch(setUser({...data.user}));
               return (<Redirect to ='/usersPosts'/>)
            }
            else{
                dispatch(setMessage(data.error));
            }
            
        })
        .catch((e)=>{
            console.log(e);
        });
        } catch{
        
        console.log('Error!');
    }
}
export const GetUsersPosts =  (page,author) => async dispatch=>{ 


    try {
        if(!author)
            return;
        const requestOptions = {
            method: 'POST',
            mode:'cors',
            headers: { 
                "Content-type": "application/json; charset=UTF-8",
            },
            body: JSON.stringify({
                    match:{
                        author:author
                    },
                    sort:{},
                    limit: 10,
                    page: page
                })
        };
        debugger;
        fetch(urlApi+'/post/filterPosts', requestOptions)
            .then(res => res.json())
            .then(data => {
                dispatch(setUserPost(data));
                dispatch(setUserLoading(true));
            })
            .catch((e)=>{
                console.log(e);
            });
        } catch{
            dispatch(setUserLoading(false));
            console.log('Error!');
        }
    }

export const signUserUp = (userInfo) => dispatch => {
    try {
        fetch(urlApi+`/users/createNewUser`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json"
            },
            body: JSON.stringify(userInfo)
        })
        .then(res => res.json())
        .then(data => {
        
            debugger;
            localStorage.setItem("token", data.currentToken);
            dispatch(setUser(data.user));
            history.push('/usersPosts');

        })
        .catch((e)=>{
            console.log(e);
        });
    } catch{
            
        console.log('Error!');
    }
}

export const autoLogin = () => dispatch => {
    try {
        fetch(`urlApi/auto_login`, {
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
                "Authorization": `Bearer ${localStorage.getItem("token")}`
            }
        })
        .then(res => res.json())
        .then(data => {
            
            localStorage.setItem("token", data.currentToken)
            dispatch(setUser(data))
        })
        .catch((e)=>{
            console.log(e);
        });
    } catch{
            
        console.log('Error!');
    }
}
