import React, { useEffect } from 'react';
import './Posts.css';
import { useSelector, useDispatch } from 'react-redux';
import {GetPosts} from '../../services/post_services';
import {Link}  from "react-router-dom";
import { Card, Icon, Image } from 'semantic-ui-react'
function Posts() {
  
  const dispatch = useDispatch();
    
  useEffect(() => {
      GetPosts(dispatch);
      }, []);
  
  const posts = useSelector(state => state.posts.posts);
  const loading = useSelector(state => state.posts.loading)
  if(loading === false)
      return<div>loading...</div>
  return (
    <div className="posts">
      {
        
        posts.map(post =>
          <Card>
            `<div className = 'list-post' key = {post.id.toString()} >
                  <div className = 'img-post'><img src= {post.UrlImg}></img></div>
                  <Link  to={`/posts/${post.id}`}><div><span>{post.Name}</span></div>  </Link>
                  <div><span>{post.Description}</span></div>
                
            </div>`
            </Card>
          
          )
         
      }
        
    </div>
  );
}

export default Posts;
