import {SET_PAGING} from '../Post/typesPost'
import{GetPosts} from '../../services/post_services';
import{SET_User_PAGING} from '../User/typeUser';
import{GetUsersPosts} from '../../services/user_services'

export const changePageMiddleware = (store)=>(next)=>(action)=>{
    debugger;
    if(action.type === SET_PAGING)
    {
        store.dispatch(GetPosts(action.payload));
    }
    return next(action);
}
    
export const changeUserPageMiddleware = (store)=>(next)=>(action)=>{
    if(action.type === SET_User_PAGING)
    {
        store.dispatch(GetUsersPosts(action.payload));
    }
    return next(action);
}
