import React from 'react';
import './Header.css';
import { useSelector, useDispatch } from 'react-redux';
import { Button } from 'semantic-ui-react'
import {Link, Redirect}  from "react-router-dom";
import { Image } from 'semantic-ui-react'
import mainlogo from '../../public_img/logo.jpg'
import loginLogo from '../../public_img/userIcon.png'
import { Dropdown, Icon } from 'semantic-ui-react'
import { useHistory } from 'react-router-dom';
import {logoutUser} from '../../services/user_services'
function Header() {
  const autorize = useSelector(state => state.user.loggedIn);
  const name = useSelector(state => state.user.user.name);
  const dispatch = useDispatch();
  const trigger = (
    
    <span>
      <Icon name='user' /> <strong>Мій профіль</strong>
    </span>
  )

  let history = useHistory();
    function RedirectToLogin (e){
      debugger;
        if(!autorize){
          e?.currentTarget.closest(false);
          return (history.push('/login'));
      }
    }
  let onChangeUserDropDown = (e,data) =>{
    debugger;
    console.log(e);
    if(data.value === 3)
    {
      
        dispatch(logoutUser());
        data.value = null;
        
    }
  }
  const options = [
    {
      key: 'user',
      text: (
        <span>
          Війшли <strong>{name}</strong>
        </span>
      ),
      disabled: true,
    },
    { key: 'posts', text: 'Ваші пости',as: Link, to: '/usersPosts'},
    { key: 'settings', text: 'Налаштування'},
    { key: 'sign-out', text: 'Вийти' },
  ]
  
  return (
    <div className='Header'>
      <div className='container-icon'>
          <Image as={Link} to='/'  src={mainlogo}  circular />
      </div>
      <div className='container-auth'>
          <div className='container-profile'>
            {
              autorize?(<Dropdown className='prof-dropdown' onChange={onChangeUserDropDown}     trigger={trigger} onClick={RedirectToLogin} options={options} />)
              :(<Dropdown className='prof-dropdown'   open={false}  trigger={trigger} onClick={RedirectToLogin} options={options} />)
              
            }
           
          </div>
      
        <div className = 'container-add-template'>
          <Button  as={Link} to="/addPosts"  className = 'button-add-temolate'>Додати оголошення</Button>
        </div>
      </div>
    </div>
  );
}

export default Header;
