import { SET_NOTES, DELETE_NOTE, NEW_NOTE, EDIT_NOTE, SET_LOADING, SET_PAGING } from './typesPost';

const initialState = {
    posts: [],
    loading: false,
    page: 0
}
export default function postsReducer(state = initialState, action) {
    switch (action.type) {
        case SET_NOTES:
            return { ...state, posts: [...action.payload] };
        case DELETE_NOTE:
            return {
                ...state, posts: [...state.notes.filter(post =>
                    post.id !== action.payload.value.id)]
            }
        case NEW_NOTE:
            {
                if (!state.posts.find(i => i._id === action.payload._id))
                    return { ...state, posts: [...state.posts, action.payload] }
                return state;
            }
        case SET_LOADING:
            return { ...state, loading: action.payload }
        case SET_PAGING:
            return { ...state, page: action.payload}
        case EDIT_NOTE:
            let posts = state.posts.map(post => {
                debugger;
                if (post.id === action.payload._id) {
                    post = action.payload
                }
                return post;
            })
            return { ...state, posts: [...posts] }
        default:
            return state;
    }
}
