import React, { useEffect } from 'react';
import './Posts.css';
import { useSelector, useDispatch } from 'react-redux';
import {GetPosts,arrayBufferToBase64} from '../../services/post_services';
import {Link}  from "react-router-dom";
import { Card, Image } from 'semantic-ui-react'
import Moment from 'react-moment'
import  'moment/locale/ru';
import { Pagination } from 'semantic-ui-react';
import {set_Page} from '../../redux/Post/actionsPost';

function Posts(props) {
  const dispatch = useDispatch();
  const page = useSelector(state => state.posts.page);
  useEffect(() => {
      dispatch(GetPosts(page));
      }, []);
  const posts = useSelector(state => state.posts.posts);
  const loading = useSelector(state => state.posts.loading);
  const onchangePage =(e, {activePage})=>{
    dispatch(set_Page(activePage-1));
  }
  debugger;
  var base64Flag = 'data:image/jpeg;base64,';
  if(loading === false)
      return<div>loading...</div>
  return (
    <div className="posts">
      <Card.Group>
      {
        posts.map(post =>
            <Card key = {post._id}>
                    
                      <Card.Content>
                        {
                          post.images?.length > 0 ? <Image className='imgpost' src= {base64Flag + arrayBufferToBase64(post?.images[0]?.data)}></Image >:<img src="/images/wireframe/image.png"/>
                        }
                       </Card.Content>
                    <Card.Content extra>
                      <Card.Header  >
                      <Link  to={`/posts/${post._id}`}><span className='posts-header'>{post.header}</span> </Link>
                    </Card.Header>
                    <div>
                      <span>{post.location +" "}</span>
                      <Moment  date ={post.dateCreated} format ="MMM Do YY"> </Moment>
                    </div>
                    </Card.Content>
            </Card>
          )
      }
          </Card.Group>
          <div className='post-paging'> <Pagination  defaultActivePage={1}  totalPages={10} onPageChange = {onchangePage} /></div>
    </div>
  );
}
  {/*
  EditPost.propTypes = {
  editMode: PropTypes.bool,
  post: PropTypes.object
  };*/}
  
  export default Posts;
