import React, { useState , useEffect } from 'react';
import './Posts.css';
import {Button,Form,} from 'semantic-ui-react'
import {Link}  from "react-router-dom";
import ImageUploading from 'react-images-uploading';
import addImg from '../../public_img/addImg.png'
import { Image } from 'semantic-ui-react'


 const PostForm  = (props)=>{
  const [modalPost, setModalPost] = useState({
    header:'',
    description:'',
    images:[],
    location:'',
    dateCreated:'',
    viewCount:0,
    tags:[],
  });
  useEffect(() => {
      setModalPost(props.Post);
  }, [props.Post]);

  const inputGroupChangeHandler = (event) => {
  setModalPost((prevState) => ({
    ...prevState,
    [event.target.id]: event.target.value
    }));
  }
  const SetImages = (event) => {
    debugger;
    setModalPost((prevState) =>({
      ...prevState,
      images:event
    }));
  }
  return (
  <Form size='huge' className='cr-up-form' onSubmit={event => {
    console.log('submit');
    event.preventDefault();
    }}>

    <Form.Group  widths={2} className='form-cr-up-post'>
      <Form.Input label='Назва'  type="text" id='header' width={8}
           name="header"value={!modalPost ? '' : modalPost.header}
          onChange={event => inputGroupChangeHandler( event)}/>
      <Form.Input label='Місце знаходження'  type="text" id='location' width={8}
        name="location"value={!modalPost ? '' : modalPost.location}
      onChange={event => inputGroupChangeHandler( event)}/>
       </Form.Group>
      <Form.Group>
      <ImageUploading
        multiple
        id='images'
        dataURLKey="data_url"
        value={!modalPost?[]:modalPost.images}
        onChange={SetImages}
        maxNumber={3}
      >
        {({
          imageList,
          onImageUpload,
          onImageRemoveAll,
          onImageUpdate,
          onImageRemove,
          isDragging,
          dragProps,
        }) => (
          // write your building UI
          <div className="upload__image-wrapper">
            <div className="upload__image-wrapper-btn-block">
              <button className = "upload__image-wrapper-btn"
                style={isDragging ? { color: 'red' } : undefined}
                onClick={onImageUpload}
                {...dragProps}
              >
                <Image src = {addImg}/>
              </button>
            </div>
            &nbsp;
            <div className ='item-images'>
            {imageList.map((image, index) => (
              <div key={index} className="image-item">
                <img src={image['data_url']} alt="" width="100" />
                <div className="image-item__btn-wrapper">
                  <button onClick={() => onImageUpdate(index)}>Update</button>
                  <button onClick={() => onImageRemove(index)}>Remove</button>
                </div>
              </div>
            ))}
            </div>
          </div>
        )}
      </ImageUploading>
      </Form.Group>
      <Form.Group>
      <Form.TextArea label='Опис'  type="text" id='description' width={16}
           name="description" value={!modalPost ? '' : modalPost.description}
          onChange={event => inputGroupChangeHandler( event)}/>
    </Form.Group>
      <div className = 'cr-up-submit-button'>
        <Button as={Link} to="/" type='submit' onClick={()=>{ props.SubmitPostForm(modalPost)}} variant="primary"   >
          Зберегти
        </Button>
      </div>
    
  </Form>
    );
};
export default PostForm;
