import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { useSelector, useDispatch } from 'react-redux';
import { Container, Header,Divider,Button } from 'semantic-ui-react';
import {Link} from 'react-router-dom';
import Moment from 'react-moment'
import  'moment/locale/ru';


const ViewPost = function (props) {

    console.log(props);
    if(!props.post)
    {
        const dispatch = useDispatch();
    
        useEffect(() => {
            GetPosts(dispatch);
            }, []);

    const posts = useSelector(state => state.posts.posts);
  
    }
   
    return (
        <div className="view-post">
            <Container fluid>
                <Header as='h2'><span>{props.post.header}</span></Header>
                <Divider className='devider'/>
                <Header as='h2'><span>Опис</span></Header>
                <p>{props.post.description}</p>
                <div className='button-view-post'>
                <Button  as={Link} to="/changePost" secondary className = 'button-change-post'>Змінити</Button>
                </div>
                
                <Divider />
            <div className='footer-post-view'>
                <span>{props.post.location +" "}</span>
                <Moment  date ={props.post.dateCreated} format ="MMM Do YY"> </Moment>
            </div>
            </Container>
        </div>
    );

}

let mapStateToProps=(state,ownProps)=>{
    debugger;
    console.log(ownProps);
    if(! state.post.posts[state.posts.posts[ownProps.match.params.id-1]])
    {
        
    }
    return{
        post:state.posts.posts[ownProps.match.params.id-1],
    }
  };
  
  ViewPost.propTypes = {
    post: PropTypes.object
  }
  const viewPost = connect(mapStateToProps,null) (ViewPost);
  export default viewPost;
